INSERT INTO `template_zzzsys_form` VALUES ('51dcdedfb431065','','SCDriver','Driver','select * from driver\nLEFT JOIN truck ON truck_id = dri_truck_id','driver','driver_id',1300,500,'SELECT \'#dri_first_name# #dri_last_name#\'','1','1','','','','1','','','','','','','','','','','','','','','','','$thisID   = \'#RECORD_ID#\';\n\n$sql = \"SELECT * FROM driver WHERE driver_id = \'$thisID\' \";\n$rs  = nuRunQuery($sql);\n \n$obj = db_fetch_object($rs);\n$address  = \"\";             \n$address .= $obj->dri_street_address_line_1.\" \";\n$address .= $obj->dri_street_address_line_2.\" \";\n$address .= $obj->dri_street_city.\" \";\n$address .= $obj->dri_street_region.\" \";\n$address .= $obj->dri_street_postal_code.\" \";\n$address .= $obj->dri_street_country;               \n\nif ( $address != \'\' ) {\n\n	$address = urlencode($address);\n	$url = \"https://maps.google.com/maps/api/geocode/json?address=$address&sensor=false\";\n\n	$ch = curl_init();\n	curl_setopt($ch, CURLOPT_URL, $url);\n	curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n	curl_setopt($ch, CURLOPT_PROXYPORT, 3128);\n	curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);\n	curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);\n\n	$response = curl_exec($ch);\n	curl_close($ch);\n	$json_response = json_decode($response);\n\n	if($json_response->results[0]->geometry->location->lng) {\n\n		$sql  = \"UPDATE driver SET \";\n		$sql .= \"dri_lng = \'\".$json_response->results[0]->geometry->location->lng.\"\' \"; \n		$sql .= \", \"; \n		$sql .= \"dri_lat = \'\".$json_response->results[0]->geometry->location->lat.\"\' \";\n		$sql .= \"WHERE driver_id = \'$obj->driver_id\' \";\n		nuRunQuery($sql);\n	}  \n}\n\n$sql = \"\n	UPDATE \n		driver_notes SET drn_date = CURDATE(), \n		drn_time = CURTIME(), \n		drn_user = \'#nu_user_id#\' \n	WHERE \n		(drn_date IS NULL OR drn_date = \'\' OR drn_date = \'0000-00-00\') \n		AND drn_driver_id = \'#RECORD_ID#\' \n\";\nnuRunQuery($sql);\n\n//Drop Box\n$sql = \"SELECT slp_php FROM zzzsys_php WHERE slp_code = \'D