rite($handle, $contents);\n		fclose($handle);\n		$response2 = $dropbox->putFile($tmp_file, $trk_rego_file_name, \"Trucks/$thisID\");\n		nuDeleteInkPickerFile($trk_rego_file_picker);\n\n	} catch (Exception $e) {\n	 \n	}\n}\n\nif (\'#trk_iap_file_picker#\' != \"\") {\n	try {\n		$trk_iap_file_name      = \'#trk_iap_file_name#\';\n		$trk_iap_file_picker = \'#trk_iap_file_picker#\';\n		$urlHandle = fopen($trk_iap_file_picker, \"rb\");\n		$contents  = stream_get_contents($urlHandle);\n		fclose($urlHandle);\n		$tmp_file = tempnam( sys_get_temp_dir(), \"nu\");\n		$handle = fopen($tmp_file, \"w\");\n		fwrite($handle, $contents);\n		fclose($handle);\n		$response2 = $dropbox->putFile($tmp_file, $trk_iap_file_name, \"Trucks/$thisID\");\n		nuDeleteInkPickerFile($trk_iap_file_picker);\n\n	} catch (Exception $e) {\n	 \n	}\n}\n     \nfunction nuDeleteInkPickerFile($sfi_filepicker) {\n        error_log(\'start deleteInkPickerFile\');\n        ob_start();\n\n        $setup = $GLOBALS[\'nuSetup\'];\n        $set_inkfilepicker_key = $Setup->set_inkfilepicker_key;\n\n        $url  = trim($sfi_filepicker);\n        $url .= \"?key=\";\n        $url .= $set_inkfilepicker_key;\n        $ch   = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $url);\n        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);\n        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, \"DELETE\");\n        try{\n            $result = curl_exec($ch);\n            $errors = curl_error($ch);\n            error_log($result); \n        }catch( Exception $e ){\n            error_log($e);\n        }\n        curl_close($ch);  \n        ob_end_clean();\n        error_log(\'end deleteInkPickerFile\'); \n}','','$(document).ready(function() {\n	if(nuFORM.tip == \'Browse\') {\n	  formatGrid();\n	}\n});\n\nfunction nuSelectImageFile() {\n    filepicker.pickAndStore(\n           {multiple: [false],mimetype: [\'image/*\']},\n          {location:\"COMPUTER\"},\n          function(InkBlobs){\n              $(\'#trk_pic_file_name\').val(Ink